'use strict';

function normalizeTag(tag) {
  if (tag == null) return '';
  return String(tag).trim();
}

function unique(arr) {
  const set = new Set();
  const out = [];
  for (const v of arr) {
    const k = v; // keep case as-is, uniqueness by exact string
    if (!set.has(k)) {
      set.add(k);
      out.push(v);
    }
  }
  return out;
}

function applyBulkTags(products, ops) {
  const add = Array.isArray(ops && ops.add) ? ops.add.map(normalizeTag).filter(Boolean) : [];
  const remove = Array.isArray(ops && ops.remove) ? ops.remove.map(normalizeTag).filter(Boolean) : [];
  const targetSkus = new Set(
    Array.isArray(ops && ops.skus)
      ? ops.skus.map((s) => String(s).trim()).filter(Boolean)
      : []
  );

  const results = [];
  let matched = 0;
  let updated = 0;
  let addedCount = 0;
  let removedCount = 0;

  for (const p of Array.isArray(products) ? products : []) {
    if (!targetSkus.size || targetSkus.has(p.sku)) {
      matched += 1;
      const before = Array.isArray(p.tags) ? [...p.tags] : [];
      let after = [...before];

      // add
      for (const t of add) {
        if (!after.includes(t)) {
          after.push(t);
          addedCount += 1;
        }
      }

      // remove
      if (remove.length) {
        const sizeBefore = after.length;
        after = after.filter((t) => !remove.includes(t));
        removedCount += sizeBefore - after.length;
      }

      after = unique(after);

      const changed = JSON.stringify(before) !== JSON.stringify(after);
      if (changed) updated += 1;

      results.push({
        sku: p.sku,
        before,
        after,
        added: add.filter((t) => !before.includes(t)),
        removed: remove.filter((t) => before.includes(t))
      });
    }
  }

  return {
    items: results,
    stats: { matched, updated, added: addedCount, removed: removedCount }
  };
}

module.exports = { applyBulkTags };

'use strict';

const { applyBulkTags } = require('../../../../lib/bulkTags');
const { getProducts } = require('../../../../lib/products');

function parseSkuList(input) {
  if (!input) return [];
  if (Array.isArray(input)) return input.map((s) => String(s).trim()).filter(Boolean);
  // split by newline, comma or whitespace
  return String(input)
    .split(/[^A-Za-z0-9_\-\.]+/g)
    .map((s) => s.trim())
    .filter(Boolean);
}

module.exports = async function handler(req, res) {
  try {
    if (req.method !== 'POST') {
      res.setHeader('Allow', 'POST');
      return res.status(405).json({ error: 'Method Not Allowed' });
    }

    const body = req.body || {};
    const skus = parseSkuList(body.skus || body.skuList || body.sku);
    const add = Array.isArray(body.add) ? body.add : (body.add ? [body.add] : []);
    const remove = Array.isArray(body.remove) ? body.remove : (body.remove ? [body.remove] : []);

    if (!skus.length) {
      return res.status(400).json({ error: 'Provide at least one SKU in skus[] or a string list.' });
    }

    const products = (await getProducts()) || [];
    const { items, stats } = applyBulkTags(products, { skus, add, remove });

    return res.status(200).json({ count: items.length, items, stats });
  } catch (err) {
    console.error('bulk-preview error', err);
    return res.status(500).json({ error: 'Bulk tag preview failed' });
  }
};

import React from 'react';
import Layout from '../../components/Layout';

export default function BulkTagsAdminPage() {
  const [skuText, setSkuText] = React.useState('');
  const [addText, setAddText] = React.useState('');
  const [removeText, setRemoveText] = React.useState('');
  const [result, setResult] = React.useState(null);
  const [error, setError] = React.useState(null);
  const [loading, setLoading] = React.useState(false);

  async function onPreview(e) {
    e.preventDefault();
    setError(null);
    setResult(null);
    setLoading(true);
    try {
      const res = await fetch('/api/products/tags/bulk-preview', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          skus: skuText,
          add: addText.split(',').map((s) => s.trim()).filter(Boolean),
          remove: removeText.split(',').map((s) => s.trim()).filter(Boolean),
        }),
      });
      const json = await res.json();
      if (!res.ok) throw new Error(json && json.error ? json.error : 'Request failed');
      setResult(json);
    } catch (err) {
      setError(err.message || String(err));
    } finally {
      setLoading(false);
    }
  }

  return (
    <Layout title="Bulk Tagging Preview">
      <div style={container}>
        <h1>Bulk Tagging Preview</h1>
        <p>Paste SKUs and specify tags to add or remove. This tool previews changes and does not persist updates.</p>

        <form onSubmit={onPreview} style={form}>
          <div style={row}> 
            <label style={label}>SKUs</label>
            <textarea
              style={textarea}
              rows={6}
              value={skuText}
              onChange={(e) => setSkuText(e.target.value)}
              placeholder="One per line or separated by commas"
            />
          </div>

          <div style={row}> 
            <label style={label}>Add tags</label>
            <input
              style={input}
              value={addText}
              onChange={(e) => setAddText(e.target.value)}
              placeholder="comma,separated,tags"
            />
          </div>

          <div style={row}> 
            <label style={label}>Remove tags</label>
            <input
              style={input}
              value={removeText}
              onChange={(e) => setRemoveText(e.target.value)}
              placeholder="comma,separated,tags"
            />
          </div>

          <button type="submit" disabled={loading} style={button}>
            {loading ? 'Previewing…' : 'Preview Changes'}
          </button>
        </form>

        {error ? (
          <div style={errorBox}>{error}</div>
        ) : null}

        {result ? (
          <div style={{ marginTop: 24 }}>
            <h2>Result</h2>
            <div style={{ color: '#374151' }}>
              Matched: <strong>{result.stats.matched}</strong>, Updated: <strong>{result.stats.updated}</strong>,
              Added tags: <strong>{result.stats.added}</strong>, Removed tags: <strong>{result.stats.removed}</strong>
            </div>
            <table style={table}>
              <thead>
                <tr>
                  <th style={th}>SKU</th>
                  <th style={th}>Before</th>
                  <th style={th}>After</th>
                  <th style={th}>Added</th>
                  <th style={th}>Removed</th>
                </tr>
              </thead>
              <tbody>
                {result.items.map((it) => (
                  <tr key={it.sku}>
                    <td style={td}><code>{it.sku}</code></td>
                    <td style={td}>{Array.isArray(it.before) ? it.before.join(', ') : ''}</td>
                    <td style={td}>{Array.isArray(it.after) ? it.after.join(', ') : ''}</td>
                    <td style={td}>{Array.isArray(it.added) ? it.added.join(', ') : ''}</td>
                    <td style={td}>{Array.isArray(it.removed) ? it.removed.join(', ') : ''}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : null}
      </div>
    </Layout>
  );
}

const container = { padding: 20, maxWidth: 960, margin: '0 auto' };
const form = { display: 'flex', flexDirection: 'column', gap: 12 };
const row = { display: 'flex', flexDirection: 'column', gap: 6 };
const label = { fontWeight: 600 };
const textarea = { width: '100%', fontFamily: 'monospace', border: '1px solid #e5e7eb', borderRadius: 6, padding: 8 };
const input = { width: '100%', border: '1px solid #e5e7eb', borderRadius: 6, padding: 8 };
const button = { width: 'fit-content', background: '#111827', color: '#fff', border: 0, borderRadius: 6, padding: '10px 14px', cursor: 'pointer' };
const errorBox = { marginTop: 12, background: '#fef2f2', color: '#991b1b', border: '1px solid #fecaca', padding: 10, borderRadius: 6 };
const table = { width: '100%', borderCollapse: 'collapse', marginTop: 12 };
const th = { textAlign: 'left', borderBottom: '1px solid #e5e7eb', padding: '8px 6px' };
const td = { borderBottom: '1px solid #f3f4f6', padding: '8px 6px', verticalAlign: 'top' };

const handler = require('../pages/api/products/tags/bulk-preview.js');
const sampleProducts = require('../lib/sampleProducts');

function createMockRes() {
  const headers = {};
  let statusCode = 200;
  let body;
  const res = {
    setHeader: (k, v) => { headers[k] = v; },
    status: (c) => { statusCode = c; return res; },
    json: (b) => { body = b; return res; },
  };
  Object.defineProperty(res, 'headers', { get: () => headers });
  Object.defineProperty(res, 'statusCode', { get: () => statusCode });
  Object.defineProperty(res, 'body', { get: () => body });
  return res;
}

describe('POST /api/products/tags/bulk-preview', () => {
  test('previews adding a tag for provided SKUs', async () => {
    const tag = '__bulk_tag__';
    const skus = sampleProducts.slice(0, 2).map((p) => p.sku);

    const req = { method: 'POST', body: { skus, add: [tag] } };
    const res = createMockRes();

    await handler(req, res);

    expect(res.statusCode).toBe(200);
    expect(res.body).toBeDefined();
    expect(res.body.count).toBe(2);
    expect(res.body.stats.matched).toBe(2);
    expect(res.body.stats.added).toBeGreaterThan(0);
    for (const item of res.body.items) {
      expect(item.after).toEqual(expect.arrayContaining([tag]));
    }
  });

  test('rejects non-POST methods', async () => {
    const req = { method: 'GET' };
    const res = createMockRes();
    await handler(req, res);
    expect(res.statusCode).toBe(405);
    expect(res.headers.Allow).toBe('POST');
  });

  test('requires at least one SKU', async () => {
    const req = { method: 'POST', body: { add: ['x'] } };
    const res = createMockRes();
    await handler(req, res);
    expect(res.statusCode).toBe(400);
    expect(res.body.error).toMatch(/Provide at least one SKU/);
  });
});

const { applyBulkTags } = require('../lib/bulkTags');

describe('applyBulkTags', () => {
  test('adds and removes tags without mutating input', () => {
    const products = [
      { sku: 'A', tags: ['alpha', 'beta'] },
      { sku: 'B', tags: [] },
    ];

    const res = applyBulkTags(products, { skus: ['A', 'B'], add: ['new'], remove: ['beta'] });

    // Input not mutated
    expect(products[0].tags).toEqual(['alpha', 'beta']);

    // Stats
    expect(res.stats.matched).toBe(2);
    expect(res.stats.updated).toBe(2);
    expect(res.stats.added).toBe(2 - 0); // 'new' added to both
    expect(res.stats.removed).toBe(1); // 'beta' removed from A only

    // Items
    const a = res.items.find((i) => i.sku === 'A');
    const b = res.items.find((i) => i.sku === 'B');

    expect(a.before).toEqual(['alpha', 'beta']);
    expect(a.after).toEqual(expect.arrayContaining(['alpha', 'new']));
    expect(a.after).not.toEqual(expect.arrayContaining(['beta']));

    expect(b.before).toEqual([]);
    expect(b.after).toEqual(['new']);
  });

  test('handles empty ops gracefully', () => {
    const products = [ { sku: 'X', tags: ['t'] } ];
    const res = applyBulkTags(products, { skus: ['X'] });
    expect(res.stats.matched).toBe(1);
    expect(res.stats.updated).toBe(0);
    expect(res.items[0].after).toEqual(['t']);
  });
});
