/*
  Sample catalog data with attribute groups and basic variant fields.
  CommonJS exports so it can be used by Next.js pages and Node-based tests.
*/

const attributeGroups = [
  {
    code: "basics",
    name: "Basics",
    attributes: [
      {
        code: "color",
        name: "Color",
        type: "select",
        options: [
          { code: "red", label: "Red" },
          { code: "blue", label: "Blue" },
          { code: "green", label: "Green" }
        ]
      },
      {
        code: "size",
        name: "Size",
        type: "select",
        options: [
          { code: "s", label: "Small" },
          { code: "m", label: "Medium" },
          { code: "l", label: "Large" }
        ]
      }
    ]
  },
  {
    code: "materials",
    name: "Materials",
    attributes: [
      {
        code: "material",
        name: "Material",
        type: "select",
        options: [
          { code: "cotton", label: "Cotton" },
          { code: "wool", label: "Wool" },
          { code: "poly", label: "Polyester" }
        ]
      }
    ]
  }
];

const products = [
  {
    id: "p-shirt-classic",
    slug: "classic-tee",
    name: "Classic Tee",
    description: "A timeless classic tee with multiple colors and sizes.",
    images: [
      "/images/products/classic-tee-red.jpg",
      "/images/products/classic-tee-blue.jpg"
    ],
    currency: "USD",
    price: 1999,
    attributes: { material: "cotton" },
    variants: [
      { id: "p-shirt-classic-red-s", sku: "CT-RED-S", price: 1999, stock: 25, options: { color: "red", size: "s" } },
      { id: "p-shirt-classic-red-m", sku: "CT-RED-M", price: 1999, stock: 30, options: { color: "red", size: "m" } },
      { id: "p-shirt-classic-blue-m", sku: "CT-BLU-M", price: 1999, stock: 12, options: { color: "blue", size: "m" } },
      { id: "p-shirt-classic-green-l", sku: "CT-GRN-L", price: 1999, stock: 5, options: { color: "green", size: "l" } }
    ]
  },
  {
    id: "p-hoodie-premium",
    slug: "premium-hoodie",
    name: "Premium Hoodie",
    description: "Cozy premium hoodie with soft-touch materials.",
    images: [
      "/images/products/premium-hoodie-black.jpg"
    ],
    currency: "USD",
    price: 4999,
    attributes: { material: "wool" },
    variants: [
      { id: "p-hoodie-premium-red-m", sku: "PH-RED-M", price: 4999, stock: 8, options: { color: "red", size: "m" } },
      { id: "p-hoodie-premium-blue-l", sku: "PH-BLU-L", price: 4999, stock: 0, options: { color: "blue", size: "l" } },
      { id: "p-hoodie-premium-green-m", sku: "PH-GRN-M", price: 4999, stock: 3, options: { color: "green", size: "m" } }
    ]
  },
  {
    id: "p-joggers-lite",
    slug: "lite-joggers",
    name: "Lite Joggers",
    description: "Breathable joggers for everyday comfort.",
    images: [
      "/images/products/lite-joggers-gray.jpg"
    ],
    currency: "USD",
    price: 3999,
    attributes: { material: "poly" },
    variants: [
      { id: "p-joggers-lite-blue-s", sku: "LJ-BLU-S", price: 3999, stock: 40, options: { color: "blue", size: "s" } },
      { id: "p-joggers-lite-blue-m", sku: "LJ-BLU-M", price: 3999, stock: 22, options: { color: "blue", size: "m" } },
      { id: "p-joggers-lite-blue-l", sku: "LJ-BLU-L", price: 3999, stock: 10, options: { color: "blue", size: "l" } }
    ]
  }
];

function getSampleAttributes() {
  return JSON.parse(JSON.stringify(attributeGroups));
}

function getSampleProducts() {
  return JSON.parse(JSON.stringify(products));
}

function getSampleCatalog() {
  return { attributes: getSampleAttributes(), products: getSampleProducts() };
}

module.exports = { getSampleAttributes, getSampleProducts, getSampleCatalog };

const { getSampleCatalog } = require("../../../lib/sampleData");

export default function handler(req, res) {
  const catalog = getSampleCatalog();
  res.status(200).json({
    success: true,
    attributes: catalog.attributes,
    products: catalog.products,
    meta: {
      count: catalog.products.length,
      generatedAt: new Date().toISOString()
    }
  });
}

import React from "react";

function Stat({ label, value }) {
  return (
    <div style={{ padding: 12, border: "1px solid #e5e7eb", borderRadius: 8, background: "#fff" }}>
      <div style={{ color: "#6b7280", fontSize: 12, textTransform: "uppercase", letterSpacing: 1 }}>{label}</div>
      <div style={{ fontSize: 20, fontWeight: 700 }}>{value}</div>
    </div>
  );
}

function VariantsTable({ variants }) {
  return (
    <table style={{ width: "100%", borderCollapse: "collapse" }}>
      <thead>
        <tr>
          <th style={{ textAlign: "left", borderBottom: "1px solid #e5e7eb", padding: "8px 4px" }}>SKU</th>
          <th style={{ textAlign: "left", borderBottom: "1px solid #e5e7eb", padding: "8px 4px" }}>Options</th>
          <th style={{ textAlign: "right", borderBottom: "1px solid #e5e7eb", padding: "8px 4px" }}>Price</th>
          <th style={{ textAlign: "right", borderBottom: "1px solid #e5e7eb", padding: "8px 4px" }}>Stock</th>
        </tr>
      </thead>
      <tbody>
        {variants.map((v) => (
          <tr key={v.id}>
            <td style={{ padding: "8px 4px", borderBottom: "1px solid #f3f4f6" }}>{v.sku}</td>
            <td style={{ padding: "8px 4px", borderBottom: "1px solid #f3f4f6" }}>
              {Object.entries(v.options).map(([k, val]) => (
                <span key={k} style={{ display: "inline-block", marginRight: 8, padding: "2px 6px", background: "#f3f4f6", borderRadius: 4, fontSize: 12 }}>
                  {k}: {val}
                </span>
              ))}
            </td>
            <td style={{ padding: "8px 4px", textAlign: "right", borderBottom: "1px solid #f3f4f6" }}>
              ${(v.price / 100).toFixed(2)}
            </td>
            <td style={{ padding: "8px 4px", textAlign: "right", borderBottom: "1px solid #f3f4f6" }}>{v.stock}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default function AdminDashboard() {
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [data, setData] = React.useState({ products: [], attributes: [] });

  React.useEffect(() => {
    let isMounted = true;
    async function load() {
      try {
        const res = await fetch("/api/admin/products");
        if (!res.ok) throw new Error("Failed to load products");
        const json = await res.json();
        if (isMounted) {
          setData({ products: json.products || [], attributes: json.attributes || [] });
        }
      } catch (e) {
        if (isMounted) setError(e.message || String(e));
      } finally {
        if (isMounted) setLoading(false);
      }
    }
    load();
    return () => {
      isMounted = false;
    };
  }, []);

  return (
    <div style={{ maxWidth: 1100, margin: "0 auto", padding: 24 }}>
      <h1 style={{ fontSize: 28, fontWeight: 800, marginBottom: 8 }}>PIM Admin</h1>
      <p style={{ color: "#6b7280", marginBottom: 16 }}>View products, attribute groups, and basic variants.</p>

      {loading && <div>Loading…</div>}
      {error && <div style={{ color: "#b91c1c" }}>Error: {error}</div>}

      {!loading && !error && (
        <div>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(3, minmax(0,1fr))", gap: 12, marginBottom: 24 }}>
            <Stat label="Products" value={data.products.length} />
            <Stat label="Attribute Groups" value={data.attributes.length} />
            <Stat label="Total Variants" value={data.products.reduce((acc, p) => acc + (p.variants ? p.variants.length : 0), 0)} />
          </div>

          <div style={{ marginBottom: 24 }}>
            <h2 style={{ fontSize: 20, fontWeight: 700, marginBottom: 8 }}>Attribute Groups</h2>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(2, minmax(0,1fr))", gap: 12 }}>
              {data.attributes.map((group) => (
                <div key={group.code} style={{ border: "1px solid #e5e7eb", borderRadius: 8, padding: 12, background: "#fff" }}>
                  <div style={{ fontWeight: 600, marginBottom: 4 }}>{group.name}</div>
                  <div style={{ color: "#6b7280", fontSize: 12, marginBottom: 8 }}>Code: {group.code}</div>
                  <div>
                    {group.attributes.map((attr) => (
                      <div key={attr.code} style={{ marginBottom: 6 }}>
                        <div style={{ fontWeight: 500 }}>{attr.name} <span style={{ color: "#6b7280" }}>({attr.code})</span></div>
                        {attr.options && attr.options.length > 0 && (
                          <div style={{ marginTop: 4 }}>
                            {attr.options.map((opt) => (
                              <span key={opt.code} style={{ display: "inline-block", marginRight: 6, marginBottom: 4, padding: "2px 6px", background: "#f3f4f6", borderRadius: 4, fontSize: 12 }}>
                                {opt.label}
                              </span>
                            ))}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div>
            <h2 style={{ fontSize: 20, fontWeight: 700, marginBottom: 8 }}>Products</h2>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(1, minmax(0,1fr))", gap: 12 }}>
              {data.products.map((product) => (
                <div key={product.id} style={{ border: "1px solid #e5e7eb", borderRadius: 8, padding: 16, background: "#fff" }}>
                  <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", marginBottom: 8 }}>
                    <div>
                      <div style={{ fontSize: 18, fontWeight: 700 }}>{product.name}</div>
                      <div style={{ color: "#6b7280", fontSize: 12 }}>ID: {product.id} · Slug: {product.slug}</div>
                    </div>
                    <div style={{ color: "#111827", fontWeight: 700 }}>${(product.price / 100).toFixed(2)}</div>
                  </div>
                  <p style={{ color: "#374151", marginBottom: 12 }}>{product.description}</p>
                  {product.attributes && (
                    <div style={{ marginBottom: 12 }}>
                      {Object.entries(product.attributes).map(([k, v]) => (
                        <span key={k} style={{ display: "inline-block", marginRight: 8, padding: "2px 6px", background: "#eef2ff", borderRadius: 4, fontSize: 12 }}>
                          {k}: {v}
                        </span>
                      ))}
                    </div>
                  )}
                  {product.variants && product.variants.length > 0 && (
                    <div>
                      <div style={{ fontWeight: 600, marginBottom: 6 }}>Variants ({product.variants.length})</div>
                      <VariantsTable variants={product.variants} />
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

/*
  Minimal test without external dependencies. Run with:
  node tests/sampleData.test.js
*/
const assert = require("assert");
const { getSampleCatalog } = require("../lib/sampleData");

(function () {
  const { attributes, products } = getSampleCatalog();

  assert(Array.isArray(attributes), "attributes should be an array");
  assert(attributes.length > 0, "attributes should not be empty");
  attributes.forEach((group) => {
    assert(group.code && group.name, "attribute group must have code and name");
    assert(Array.isArray(group.attributes) && group.attributes.length > 0, "group.attributes must be non-empty");
    group.attributes.forEach((attr) => {
      assert(attr.code && attr.name, "attribute must have code and name");
    });
  });

  assert(Array.isArray(products), "products should be an array");
  assert(products.length > 0, "products should not be empty");

  const attrCodes = new Set(attributes.flatMap((g) => g.attributes.map((a) => a.code)));

  products.forEach((p) => {
    assert(p.id && p.slug && p.name, "product must have id, slug, name");
    assert(Array.isArray(p.variants) && p.variants.length > 0, "product must have at least one variant");
    p.variants.forEach((v) => {
      assert(v.id && v.sku, "variant must have id and sku");
      assert(typeof v.price === "number", "variant.price must be number");
      assert(v.options && typeof v.options === "object", "variant.options must be an object");
      Object.keys(v.options).forEach((k) => {
        assert(attrCodes.has(k), `variant option key ${k} must reference a defined attribute`);
      });
    });
  });

  console.log("OK: sampleData provides valid catalog with attribute groups and variants");
})();
