const state = {
  groups: [
    {
      id: 'gen',
      name: 'General',
      attributes: [
        { id: 'brand', name: 'Brand', type: 'text' },
        { id: 'color', name: 'Color', type: 'text' },
        { id: 'material', name: 'Material', type: 'text' }
      ]
    },
    {
      id: 'dims',
      name: 'Dimensions',
      attributes: [
        { id: 'width', name: 'Width', type: 'number', unit: 'cm' },
        { id: 'height', name: 'Height', type: 'number', unit: 'cm' },
        { id: 'depth', name: 'Depth', type: 'number', unit: 'cm' }
      ]
    }
  ],
  updatedAt: Date.now()
};

function uid(prefix = 'id') {
  const rand = Math.random().toString(36).slice(2, 8);
  const t = Date.now().toString(36);
  return `${prefix}_${rand}${t}`;
}

function touch() {
  state.updatedAt = Date.now();
}

function deepClone(obj) {
  return JSON.parse(JSON.stringify(obj));
}

function getSnapshot() {
  return deepClone(state);
}

function findGroupIndex(id) {
  return state.groups.findIndex((g) => g.id === id);
}

function listGroups() {
  return deepClone(state.groups);
}

function addGroup(name) {
  if (!name || typeof name !== 'string') throw new Error('Invalid name');
  const g = { id: uid('grp'), name: name.trim(), attributes: [] };
  state.groups.push(g);
  touch();
  return deepClone(g);
}

function renameGroup(id, name) {
  const i = findGroupIndex(id);
  if (i === -1) throw new Error('Group not found');
  state.groups[i].name = String(name || '').trim();
  touch();
  return deepClone(state.groups[i]);
}

function deleteGroup(id) {
  const i = findGroupIndex(id);
  if (i === -1) throw new Error('Group not found');
  const [removed] = state.groups.splice(i, 1);
  touch();
  return deepClone(removed);
}

function addAttribute(groupId, attribute) {
  const i = findGroupIndex(groupId);
  if (i === -1) throw new Error('Group not found');
  const attr = Object.assign({ id: uid('attr'), type: 'text' }, attribute || {});
  if (!attr.name) throw new Error('Attribute name required');
  state.groups[i].attributes.push(attr);
  touch();
  return deepClone(attr);
}

function updateAttribute(groupId, attrId, patch) {
  const i = findGroupIndex(groupId);
  if (i === -1) throw new Error('Group not found');
  const j = state.groups[i].attributes.findIndex((a) => a.id === attrId);
  if (j === -1) throw new Error('Attribute not found');
  Object.assign(state.groups[i].attributes[j], patch || {});
  touch();
  return deepClone(state.groups[i].attributes[j]);
}

function deleteAttribute(groupId, attrId) {
  const i = findGroupIndex(groupId);
  if (i === -1) throw new Error('Group not found');
  const j = state.groups[i].attributes.findIndex((a) => a.id === attrId);
  if (j === -1) throw new Error('Attribute not found');
  const [removed] = state.groups[i].attributes.splice(j, 1);
  touch();
  return deepClone(removed);
}

module.exports = {
  getSnapshot,
  listGroups,
  addGroup,
  renameGroup,
  deleteGroup,
  addAttribute,
  updateAttribute,
  deleteAttribute
};

const store = require('../../lib/data/attributesStore');

export default async function handler(req, res) {
  try {
    if (req.method === 'GET') {
      const snap = store.getSnapshot();
      return res.status(200).json({ ok: true, data: { groups: snap.groups, updatedAt: snap.updatedAt } });
    }

    const body = req.body || {};
    const op = body.op;

    if (!op) {
      return res.status(400).json({ ok: false, error: 'Missing op' });
    }

    switch (req.method) {
      case 'POST': {
        let result = null;
        if (op === 'addGroup') {
          result = store.addGroup(body.name);
        } else if (op === 'addAttribute') {
          result = store.addAttribute(body.groupId, body.attribute);
        } else {
          return res.status(400).json({ ok: false, error: 'Unsupported op for POST' });
        }
        const snap = store.getSnapshot();
        return res.status(200).json({ ok: true, result, data: { groups: snap.groups, updatedAt: snap.updatedAt } });
      }
      case 'PUT': {
        let result = null;
        if (op === 'renameGroup') {
          result = store.renameGroup(body.id, body.name);
        } else if (op === 'updateAttribute') {
          result = store.updateAttribute(body.groupId, body.attrId, body.patch);
        } else {
          return res.status(400).json({ ok: false, error: 'Unsupported op for PUT' });
        }
        const snap = store.getSnapshot();
        return res.status(200).json({ ok: true, result, data: { groups: snap.groups, updatedAt: snap.updatedAt } });
      }
      case 'DELETE': {
        let result = null;
        if (op === 'deleteGroup') {
          result = store.deleteGroup(body.id);
        } else if (op === 'deleteAttribute') {
          result = store.deleteAttribute(body.groupId, body.attrId);
        } else {
          return res.status(400).json({ ok: false, error: 'Unsupported op for DELETE' });
        }
        const snap = store.getSnapshot();
        return res.status(200).json({ ok: true, result, data: { groups: snap.groups, updatedAt: snap.updatedAt } });
      }
      default:
        return res.status(405).json({ ok: false, error: 'Method not allowed' });
    }
  } catch (err) {
    console.error('API /attributes error', err);
    return res.status(500).json({ ok: false, error: err.message || 'Internal error' });
  }
}

import React, { useEffect, useMemo, useState } from 'react';

function Field({ label, children }) {
  return (
    <label style={{ display: 'block', marginBottom: 8 }}>
      <div style={{ fontSize: 12, color: '#555' }}>{label}</div>
      {children}
    </label>
  );
}

function Section({ title, children, actions }) {
  return (
    <section style={{ border: '1px solid #eee', borderRadius: 8, padding: 16, marginBottom: 16 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 12 }}>
        <h3 style={{ margin: 0 }}>{title}</h3>
        <div>{actions}</div>
      </div>
      {children}
    </section>
  );
}

export default function AttributesAdminPage() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [groups, setGroups] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState('');

  const selectedGroup = useMemo(() => groups.find((g) => g.id === selectedGroupId) || groups[0], [groups, selectedGroupId]);

  async function refresh() {
    setLoading(true);
    setError('');
    try {
      const r = await fetch('/api/attributes');
      const json = await r.json();
      if (!json.ok) throw new Error(json.error || 'Failed');
      setGroups(json.data.groups || []);
      if (!selectedGroupId && (json.data.groups || []).length > 0) {
        setSelectedGroupId(json.data.groups[0].id);
      }
    } catch (e) {
      setError(e.message || 'Error');
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    refresh();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  async function call(op, payload = {}, method = 'POST') {
    setLoading(true);
    setError('');
    try {
      const r = await fetch('/api/attributes', {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ op, ...payload })
      });
      const json = await r.json();
      if (!json.ok) throw new Error(json.error || 'Request failed');
      setGroups(json.data.groups || []);
      return json;
    } catch (e) {
      setError(e.message || 'Error');
    } finally {
      setLoading(false);
    }
  }

  async function handleAddGroup(e) {
    e.preventDefault();
    const fd = new FormData(e.currentTarget);
    const name = (fd.get('name') || '').toString().trim();
    if (!name) return;
    const res = await call('addGroup', { name }, 'POST');
    if (res && res.result && res.result.id) {
      setSelectedGroupId(res.result.id);
      e.currentTarget.reset();
    }
  }

  async function handleRenameGroup(id, name) {
    if (!name || !id) return;
    await call('renameGroup', { id, name }, 'PUT');
  }

  async function handleDeleteGroup(id) {
    if (!id) return;
    const ok = window.confirm('Delete this group and all its attributes?');
    if (!ok) return;
    await call('deleteGroup', { id }, 'DELETE');
    setSelectedGroupId('');
  }

  async function handleAddAttribute(e) {
    e.preventDefault();
    if (!selectedGroup) return;
    const fd = new FormData(e.currentTarget);
    const name = (fd.get('name') || '').toString().trim();
    const type = (fd.get('type') || 'text').toString();
    const unit = (fd.get('unit') || '').toString().trim();
    if (!name) return;
    await call('addAttribute', { groupId: selectedGroup.id, attribute: { name, type, unit: unit || undefined } }, 'POST');
    e.currentTarget.reset();
  }

  async function handleDeleteAttr(attrId) {
    if (!selectedGroup) return;
    await call('deleteAttribute', { groupId: selectedGroup.id, attrId }, 'DELETE');
  }

  function GroupSelector() {
    return (
      <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
        <select
          value={selectedGroup ? selectedGroup.id : ''}
          onChange={(e) => setSelectedGroupId(e.target.value)}
          style={{ padding: 8 }}
        >
          {groups.map((g) => (
            <option key={g.id} value={g.id}>
              {g.name}
            </option>
          ))}
        </select>
        {selectedGroup && (
          <button onClick={() => handleDeleteGroup(selectedGroup.id)} style={{ padding: '6px 10px', background: '#fff2f0', border: '1px solid #ffccc7', color: '#cf1322' }}>
            Delete Group
          </button>
        )}
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 900, margin: '24px auto', padding: '0 16px', fontFamily: 'system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif' }}>
      <h1>Attribute Groups</h1>
      {error && (
        <div style={{ background: '#fff1f0', border: '1px solid #ffa39e', color: '#a8071a', padding: 12, borderRadius: 6, marginBottom: 12 }}>{error}</div>
      )}
      {loading && <div style={{ marginBottom: 12 }}>Loadingâ€¦</div>}

      <Section
        title="Groups"
        actions={<GroupSelector />}
      >
        <form onSubmit={handleAddGroup} style={{ display: 'flex', gap: 8, alignItems: 'flex-end', flexWrap: 'wrap' }}>
          <Field label="New group name">
            <input name="name" placeholder="e.g. SEO" required style={{ padding: 8, minWidth: 240 }} />
          </Field>
          <button type="submit" style={{ padding: '8px 12px' }}>Add Group</button>
        </form>
        {selectedGroup && (
          <div style={{ marginTop: 16 }}>
            <Field label="Rename selected group">
              <input
                defaultValue={selectedGroup.name}
                onBlur={(e) => handleRenameGroup(selectedGroup.id, e.target.value)}
                style={{ padding: 8, minWidth: 240 }}
              />
            </Field>
          </div>
        )}
      </Section>

      {selectedGroup && (
        <Section title={`Attributes in "+${selectedGroup.name}+"`}>
          <form onSubmit={handleAddAttribute} style={{ display: 'flex', gap: 8, alignItems: 'flex-end', flexWrap: 'wrap' }}>
            <Field label="Name">
              <input name="name" placeholder="e.g. SKU" required style={{ padding: 8, minWidth: 200 }} />
            </Field>
            <Field label="Type">
              <select name="type" defaultValue="text" style={{ padding: 8 }}>
                <option value="text">Text</option>
                <option value="number">Number</option>
                <option value="boolean">Boolean</option>
                <option value="select">Select</option>
              </select>
            </Field>
            <Field label="Unit (optional)">
              <input name="unit" placeholder="e.g. cm" style={{ padding: 8, width: 120 }} />
            </Field>
            <button type="submit" style={{ padding: '8px 12px' }}>Add Attribute</button>
          </form>

          <div style={{ marginTop: 16 }}>
            {selectedGroup.attributes.length === 0 ? (
              <div style={{ color: '#888' }}>No attributes yet.</div>
            ) : (
              <div style={{ overflowX: 'auto' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr>
                      <th style={{ textAlign: 'left', padding: 8, borderBottom: '1px solid #eee' }}>Name</th>
                      <th style={{ textAlign: 'left', padding: 8, borderBottom: '1px solid #eee' }}>Type</th>
                      <th style={{ textAlign: 'left', padding: 8, borderBottom: '1px solid #eee' }}>Unit</th>
                      <th style={{ textAlign: 'right', padding: 8, borderBottom: '1px solid #eee' }}>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {selectedGroup.attributes.map((a) => (
                      <tr key={a.id}>
                        <td style={{ padding: 8 }}>
                          <input
                            defaultValue={a.name}
                            onBlur={(e) => call('updateAttribute', { groupId: selectedGroup.id, attrId: a.id, patch: { name: e.target.value } }, 'PUT')}
                            style={{ padding: 6, width: '100%' }}
                          />
                        </td>
                        <td style={{ padding: 8 }}>
                          <select
                            defaultValue={a.type}
                            onChange={(e) => call('updateAttribute', { groupId: selectedGroup.id, attrId: a.id, patch: { type: e.target.value } }, 'PUT')}
                            style={{ padding: 6 }}
                          >
                            <option value="text">Text</option>
                            <option value="number">Number</option>
                            <option value="boolean">Boolean</option>
                            <option value="select">Select</option>
                          </select>
                        </td>
                        <td style={{ padding: 8 }}>
                          <input
                            defaultValue={a.unit || ''}
                            onBlur={(e) => call('updateAttribute', { groupId: selectedGroup.id, attrId: a.id, patch: { unit: e.target.value || undefined } }, 'PUT')}
                            placeholder="-"
                            style={{ padding: 6, width: 120 }}
                          />
                        </td>
                        <td style={{ padding: 8, textAlign: 'right' }}>
                          <button onClick={() => handleDeleteAttr(a.id)} style={{ padding: '6px 10px', background: '#fff2f0', border: '1px solid #ffccc7', color: '#cf1322' }}>Delete</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </Section>
      )}

      <div style={{ marginTop: 24, color: '#888', fontSize: 12 }}>
        Tip: This demo stores attributes in memory on the server. Deploying on serverless may reset data between cold starts.
      </div>
    </div>
  );
}

const assert = require('assert');
const store = require('../lib/data/attributesStore');

// Minimal smoke tests for the in-memory attribute store
(function run() {
  const before = store.getSnapshot();
  const g = store.addGroup('Test Group');
  assert.ok(g.id && g.name === 'Test Group');

  const attr = store.addAttribute(g.id, { name: 'Rating', type: 'number' });
  assert.ok(attr.id && attr.type === 'number');

  const updated = store.updateAttribute(g.id, attr.id, { unit: 'stars' });
  assert.strictEqual(updated.unit, 'stars');

  const renamed = store.renameGroup(g.id, 'Renamed Group');
  assert.strictEqual(renamed.name, 'Renamed Group');

  const delAttr = store.deleteAttribute(g.id, attr.id);
  assert.strictEqual(delAttr.id, attr.id);

  const delGroup = store.deleteGroup(g.id);
  assert.strictEqual(delGroup.id, g.id);

  const after = store.getSnapshot();
  assert.ok(Array.isArray(after.groups));

  // Ensure initial groups are still present
  assert.ok(before.groups[0] && after.groups[0]);
})();
